import React, { Component } from "react";
import {
    Navbar,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Button

  } from "react-bootstrap";


//Small JSX Component exporting a button that changes looks when it's clicked
class OverviewCard extends Component {
  //Constructor for defining start settings in this.state and binding functions

  constructor(props) {
    //properties given to us by other components are connected with "props"
    super(props);
    //You need to bind a function in the constructor to call it throughout the class
    this.onEdit = this.onEdit.bind(this);

    //This is were our start settings are defined:
    //We want our Edit button to show "off"/false
    this.state = {
      isEditing: false
    };
  }

  onEdit(ev) {
    //New State is set
    this.setState( () => ({ isEditing: !this.state.isEditing }));
  }
    //Here is where our HTML-Markup is designed, in this case just our Edit Button
  render() {
    //The value of isEditing is called from the state
    const { isEditing } = this.state;
    //Our text is called with this.props;
    //const{text}= this.props.text;
    //Here starts our HTML, Javascript is marked with "{}" brackets.
    return (
      <div>
	<div class="container-fluid mt-4 text-muted">
	  <div class="row">


		<div class="col-xl-3 col-md-6 order-md-1 order-xl-1 mb-4">
		  <div class="card border-warning shadow rounded-pill">
			<div class="card-body">
			  <div class="row align-items-center">
				<div class="col">
				  <div class="font-weight-bold text-warning text-uppercase">Current Assignment</div>
				  <div class="h5 mb-0">React</div>
				</div>
				<div class="col-auto">
				  <i class="fas fa-calendar fa-2x"></i>
				</div>
			  </div>
			</div>
		  </div>
		</div>


		<div class="col-xl-3 col-md-6 order-md-3 order-xl-2 mb-4">
		  <div class="card border-success shadow rounded-pill">
			<div class="card-body">
			  <div class="row align-items-center">
				<div class="col">
				  <div class="font-weight-bold text-success text-uppercase">Last Assignment</div>
				  <div class="h5 mb-0">MongoDB</div>
				</div>
				<div class="col-auto">
				  <i class="fas fa-calendar fa-2x"></i>
				</div>
			  </div>
			</div>
		  </div>
		</div>


		<div class="col-xl-3 col-md-6 order-md-2 order-xl-3 mb-4">
		  <div class="card border-primary shadow">
			<div class="card-body">
			  <div class="row align-items-center">
				<div class="col">
				  <div class="font-weight-bold text-primary text-uppercase">My Task</div>
				  <div class="h5 mb-0">Task 4.2</div>
				</div>
				<div class="col-auto">
				  <i class="fas fa-clipboard-list fa-2x"></i>
				</div>
			  </div>
			</div>
		  </div>
		</div>


		<div class="col-xl-3 col-md-6 order-md-4 order-xl-4 mb-4">
		  <div class="card border-danger shadow">
			<div class="card-body">
			  <div class="row align-items-center">
				<div class="col">
				  <div class="font-weight-bold text-danger text-uppercase">Unread Messages</div>
				  <div class="h5 mb-0">15</div>
				</div>
				<div class="col-auto">
				  <i class="fas fa-comments fa-2x"></i>
				</div>
			  </div>
			</div>
		  </div>
		</div>
	  </div>
	</div>	

      </div>
    );
  }
}

export default OverviewCard ;
