import React, { Component } from "react";
import {
    Form,
    Button,
    Modal
   } from "react-bootstrap";
//Small JSX Component exporting a button that changes looks when it's clicked
class UserModal extends Component {
  //Constructor for defining start settings in this.state and binding functions
constructor(props, context) {
    super(props, context);

    this.handleShow = this.handleShow.bind(this);
    this.handleClose = this.handleClose.bind(this);
    this.onSave = this.onSave.bind(this);
    this.state = {
      name: "xxx",
      age: 100,
      gender: "d",
      studyGroup: "NEC",
      show: true,
    };
  }

  handleClose() {
    this.setState({ show: false });
  }

  handleShow() {
    this.setState({ show: true });
  }

  onSave = event => {
    event.preventDefault();

    const data = new FormData(event.target);
    console.log(data);
    console.log(data[0]);
    console.log(data.value[0]);
    //New State is set
    this.setState( () => ({ 
        name: data.age,
        age: data[1],
        gender:data[2], 
        studyGroup: data[3],
        show: false
    }) );
  }
    //Here is where our HTML-Markup is designed, in this case just our Edit Button
  render() {
    return (
      <div>
        <Modal show={this.state.show} onHide={this.handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Modal heading</Modal.Title>
          </Modal.Header>
          <Modal.Body>
          <Form>
  <Form.Group controlId="NameID">
    <Form.Label>Name</Form.Label>
    <Form.Control value={this.state.value} type="name" placeholder="Enter name" />
  </Form.Group>

  <Form.Group controlId="AgeID">
    <Form.Label>Age</Form.Label>
    <Form.Control value={this.state.value} type="age" placeholder="Age" />
  </Form.Group>



  <Form.Group controlId="GenderID">
      <Form.Label>Gender</Form.Label>
      <Form.Control value={this.state.value}  as="select">
        <option value="Female">Female</option>
        <option value="Male">Male</option>
        <option value="Diverse">Diverse</option>
      </Form.Control>
    </Form.Group>


  <Form.Group controlId="StudyGroupID">
    <Form.Label>Study Group</Form.Label>
    <Form.Control value={this.state.value} type="studyGroup" placeholder="Name of your study group" />
  </Form.Group>


</Form>
  </Modal.Body>
          <Modal.Footer>
            <Button variant="secondary" onClick={this.handleClose}>
              Close
            </Button>
            <Button variant="primary" onClick={this.onSave}>
              Save Changes
            </Button>
          </Modal.Footer>
        </Modal>   

      </div>
    );
  }
}

export default UserModal;
