import React, { Component } from "react";
import {
    Navbar,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Button

  } from "react-bootstrap";
  import logo from "./public/soco-logo.png";
  import person from "./public/person.png";


//Small JSX Component exporting a button that changes looks when it's clicked
class WebNavbar extends Component {
  //Constructor for defining start settings in this.state and binding functions

  constructor(props) {
    //properties given to us by other components are connected with "props"
    super(props);
    //You need to bind a function in the constructor to call it throughout the class
    this.onEdit = this.onEdit.bind(this);

    //This is were our start settings are defined:
    //We want our Edit button to show "off"/false
    this.state = {
      isEditing: false
    };
  }

  onEdit(ev) {
    //New State is set
    this.setState( () => ({ isEditing: !this.state.isEditing }));
  }
    //Here is where our HTML-Markup is designed, in this case just our Edit Button
  render() {
    //The value of isEditing is called from the state
    const { isEditing } = this.state;
    //Our text is called with this.props;
    //const{text}= this.props.text;
    //Here starts our HTML, Javascript is marked with "{}" brackets.
    return (
      <div>
      <nav class="navbar navbar-expand-md sticky-top navbar-light bg-light shadow-sm" id="mainNav">

      <div class="container-fluid">
		<div class="navbar-brand d-flex">
            <img src={logo} className="App-logo" alt="logo" height="35" />
			<h4 class="text-muted ml-2 mt-1">React-based User Dashboard</h4>
        </div>

		<button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navCollapse">
          <span class="navbar-toggler-icon"></span>
        </button>

        <div class="collapse navbar-collapse align-middle" id="navCollapse">
          <ul class="navbar-nav ml-auto">
			<li class="nav-item mt-1">
              <a class="nav-link" href="#">Assignment Progress</a>
            </li>
			<li class="nav-item mt-1">
              <a class="nav-link" href="#">Resources Usage</a>
            </li>
			<li class="nav-item dropdown ml-4">
              <a class="nav-link dropdown-toggle" href="#" id="userDropdown" role="button" data-toggle="dropdown">
                <span>User</span>
                <img class="rounded-circle" height="35" alt="personicon" src={person}/>
              </a>
              <div class="dropdown-menu dropdown-menu-right shadow bg-light">
				<div class="dropdown-item text-secondary">
					<p>Age: xx  Gender: x  Group: X</p>
				</div>
                <div class="dropdown-divider"></div>
                <a class="dropdown-item text-secondary" href="#" data-toggle="modal" data-target="#changeModal">
                  <i class="fas fa-exchange-alt mr-2"></i>Change User
                </a>
              </div>
            </li>
          </ul>
        </div>
      </div>
    </nav>

      </div>
    );
  }
}

export default WebNavbar;
